        elementConfig:{
          type:'text',
          placeholder:'Street'
        },
        value:''
      },
      ZipCode:{
        elementType: 'input',
        elementConfig:{
          type:'text',
          placeholder:'ZIP Code'
        },
        value:''
      },
      country:{
        elementType: 'input',
        elementConfig:{
          type:'text',
          placeholder:'Country'
        },
        value:''
      },
      email:{
        elementType: 'input',
        elementConfig:{
          type:'text',
          placeholder:'Your E-Mail'
        },
        value:''
      },
      deliveryMethod:{
        elementType: 'input',
        elementConfig:{
          options:[
            {value:'fastest', displayValue:'Fastest'},
            {value:'cheapest', displayValue:'Cheapest'}
          ]
        },
        value:''
      },
    })
    
