import React, { useState } from 'react'
import Auxiliary from '../../hoc/Auxiliary'
import Burger from '../../Components/Layout/Burger/Burger'
import BuildControls from '../../Components/Layout/Burger/BuildControls/BuildControls'
import { func } from 'prop-types'
const BurgerBuilder = () => {
  const [price,setPrice]=useState({
    salad: 0.5,
    cheese: 0.4,
    meat: 1.3,
    bacon: 0
  })
  const [ingredient,setIngredient]=useState({
    salad: 0,
    bacon: 0,
    cheese: 0,
    meat: 0
  })
  const [total,setTotal]=useState(4)
  const [purchaseable,setPurchaseable]=useState(false)
 function updatePurchaseState(ingredients){
const sum=Object.keys(ingredients)
.map(igKey=>{
  return ingredients[igKey]
})
.reduce((sum,el)=>{
  return sum + el
},0)
console.log(sum)
if(sum>0){
  setPurchaseable(true)
}
  }
 function addIngredientHandler(type){
const oldCount=ingredient[type]
const updatedCount=oldCount+1
 const updatedIngredients={
   ...ingredient
 }
 updatedIngredients[type]=updatedCount
 const priceAddition=price[type]
 const oldPrice=total
 const newPrice=oldPrice+priceAddition
 setTotal(newPrice)
 setIngredient(updatedIngredients)
 updatePurchaseState(updatedIngredients)
 console.log(updatedIngredients)
  }
function removeIngredientHandler(type){
  const oldCount=ingredient[type]
  if(oldCount<=0){
    return null
  }
  const updatedCount=oldCount-1
   const updatedIngredients={
     ...ingredient
   }
   updatedIngredients[type]=updatedCount
   const priceAddition=price[type]
   const oldPrice=total
   const newPrice=oldPrice-priceAddition
   setTotal(newPrice)
   setIngredient(updatedIngredients)
   console.log(updatedIngredients)
}
const disabledInfo={
  ...ingredient
}
for(let key in disabledInfo){
disabledInfo[key]=disabledInfo[key]<=0
}
//disabledInfo = {salad:true, meat: false, ....}
    return (
        <Auxiliary>
          <Burger ingredient={ingredient}/>
<BuildControls 
ingredientAdded={addIngredientHandler}
ingredientRemoved={removeIngredientHandler}
disabled={disabledInfo}
price={total}
purchase={purchaseable}
/>
        </Auxiliary>
    )
}

export default BurgerBuilder
