import './Order.css'
const Order = (props) => {
      //way to convert object into an array
    const [ingredients,setIngredients]=useState([])
    for(let ingredientName in props.ingredients){
        ingredients.push({
           name: ingredientName,
           amount: props.ingredients[ingredientName]
        })
    }
    console.log(ingredients)
