    return (
        <div className="Order">
            <p>Ingredients:
                {
                    ingredients.map(ig=>(
                   <p>hello {ig.name}</p>
    ))
                }
                </p>
            <p>Price: <strong>{Number.parseFloat(props.price).toFixed(2)}</strong></p>
        </div>
    )
}

export default Order
