import React, { useEffect, useState } from 'react'
import Auxiliary from '../../hoc/Auxiliary'
import Burger from '../../Components/Layout/Burger/Burger'
import BuildControls from '../../Components/Layout/Burger/BuildControls/BuildControls'
import Modal from '../../UI/Backdrop/Modal/Modal'
import OrderSummary from '../../Components/Layout/Burger/OrderSummary/OrderSummary'
import axios from '../../axios-orders'
import Spinner from '../../UI/Spinner/Spinner'
const BurgerBuilder = () => {
  const [price,setPrice]=useState({
    salad: 0.5,
    cheese: 0.4,
    meat: 1.3,
    bacon: 0
  })
  const [ingredient,setIngredient]=useState()
  
  useEffect(()=>{
    axios.get('https://my-burger-app-b263a-default-rtdb.firebaseio.com/ingredients.json')
    .then(response=>{
   setIngredient(response.data)
    })
  })
  const [total,setTotal]=useState(4)
  const [purchaseable,setPurchaseable]=useState(false)
  const [purchasing,setPurchasing]=useState(false)
const [loading,setLoading]=useState(false)
  function purchaseHandler(){
setPurchasing(true)
  }

  function cancelPurchaseHandler(){
    setPurchasing(false)
  }

  function purchaseContinueHandler(){
    setLoading(true)
    const order={
      ingredient: ingredient,
      price: total,
    customer:{
name: "Irtaza",
address:{
 city :"Gujranwala",
zipCode:"41351",
country:"Pakistan"
    },
    email:"irtaza010@gmail.com",
  }, 
    deliveryMethod: 'fastest'
  }
axios.post('/orders.json',order)
.then(response=>{
  setLoading(false)
  setPurchasing(false)
})
.catch(error=>{
  setLoading(false)
  setPurchasing(false) 
})
  }

 function updatePurchaseState(ingredients){
const sum=Object.keys(ingredients)
.map(igKey=>{
  return ingredients[igKey]
})
.reduce((sum,el)=>{
  return sum + el
},0)
console.log(sum)
if(sum>0){
  setPurchaseable(true)
}
  }
 function addIngredientHandler(type){
const oldCount=ingredient[type]
const updatedCount=oldCount+1
 const updatedIngredients={
   ...ingredient
 }
 updatedIngredients[type]=updatedCount
 const priceAddition=price[type]
 const oldPrice=total
 const newPrice=oldPrice+priceAddition
 setTotal(newPrice)
 setIngredient(updatedIngredients)
 updatePurchaseState(updatedIngredients)
 console.log(updatedIngredients)
  }
function removeIngredientHandler(type){
  const oldCount=ingredient[type]
  if(oldCount<=0){
    return null
  }
  const updatedCount=oldCount-1
   const updatedIngredients={
     ...ingredient
   }
   updatedIngredients[type]=updatedCount
   const priceAddition=price[type]
   const oldPrice=total
   const newPrice=oldPrice-priceAddition
   setTotal(newPrice)
   setIngredient(updatedIngredients)
   console.log(updatedIngredients)
}
const disabledInfo={
  ...ingredient
}
for(let key in disabledInfo){
disabledInfo[key]=disabledInfo[key]<=0
}
//disabledInfo = {salad:true, meat: false, ....}
let orderSummary=  <OrderSummary 
ingredients={ingredient}
purchaseCancelled={cancelPurchaseHandler}
purchaseContinued={purchaseContinueHandler}
price={total}
/>
if(loading){
  orderSummary=<Spinner/>
}
    return (
        <Auxiliary>
          <Modal 
          show={purchasing}
          modalClosed={cancelPurchaseHandler}
          >
          {orderSummary}
          </Modal>
          <Burger ingredient={ingredient}/>
<BuildControls 
ingredientAdded={addIngredientHandler}
ingredientRemoved={removeIngredientHandler}
disabled={disabledInfo}
price={total}
purchase={purchaseable}
ordered={purchaseHandler }
/>
        </Auxiliary>
    )
}

export default BurgerBuilder
