import React, { useEffect, useState } from 'react'
import Person from './Components/Person'
const App1 = () => {
    const [name,setName]=useState([
        {id:1, name:"Irtaza" },
        {id:2, name:"zeeja" },
        {id:3, name:"Talha" }
    ])
    useEffect(()=>{
      alert("hello")
    },[name])
function Click(index){
   let newName=[...name];
   console.log(newName)
   newName.splice(index,1)
   setName(newName)
}
function Change(event,id){
const personIndex=name.findIndex(p=>{
    return p.id===id
})
const person=name[personIndex]
person.name=event.target.value
const persons=[...name]
persons[personIndex]=person
setName(persons)


}
    return (
        <div>
            hello
            {name.map((person,index)=>(
                <Person
                name={person.name}
                click={()=>Click(index)}
                change={(event)=>Change(event,person.id)}
                key={person.id}
                />
            ))}

        </div>
    )
}

export default App1
