import React from 'react'
import './Burger.css'
import BurgerIngredients from './BurgerIngredients/BurgerIngredients'
const Burger = (props) => {
    //transformation codes
   // that is used to convert object(ingredients) into an array
   //"keys" returns array of ingredients
    let transformIngredients=Object.keys(props.ingredient)
    //"igKey" is argument that represents keys like 'salad','cheese'
    .map(igKey=>{
        //making array for ingredient which has more than 2 values, means 'Cheese: 2,salad: 3'
        return [...Array(props.ingredient[igKey])].map((_,i)=>{
            //"_" shows blank space, "i"  shows index of element(1,2,3 so on)
            return <BurgerIngredients key={igKey + i} type={igKey}/>
        })
    })
    .reduce((arr,el)=>{
        return arr.concat(el)
    },[])
    if(transformIngredients.length===0){
        transformIngredients=<p>Please start adding ingredients!</p>
    }
   
    return (
        <div className="Burger">
            <BurgerIngredients type="bread-top"/>
            {transformIngredients}
            <BurgerIngredients type="bread-bottom"/>
           
        </div>
    )
}

export default Burger
